using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        List<int> intList = new List<int>(new int[] { 1, 2, 3, 3, 2, 1 });\n\n        // Sum of the first three elements (index range 0-2)\n        int sumFirstThree = intList.Skip(0).Take(3).Sum();\n\n        // Sum of the last three elements (index range 3-5)\n        int sumLastThree = intList.Skip(3).Take(3).Sum();\n\n        Console.WriteLine($"Sum of first three: {sumFirstThree}");\n        Console.WriteLine($"Sum of last three: {sumLastThree}");\n    }\n}