using System;\nusing System.IO;\n\npublic static class DirectoryInfoExtensions\n{\n    public static FileInfo CreateNewFile(this DirectoryInfo directory, string fileName)\n    {\n        if (directory == null) throw new ArgumentNullException(nameof(directory));\n        if (string.IsNullOrEmpty(fileName)) throw new ArgumentException("File name cannot be null or empty", nameof(fileName));\n\n        return new FileInfo(Path.Combine(directory.FullName, fileName));\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        var dir = new DirectoryInfo(@"C:\Temp");\n        var file = dir.CreateNewFile("file.ext");\n\n        // Now you can work with the 'file' object as needed.\n    }\n}