protected override void Render(HtmlTextWriter writer)\n{\n    // Create a StringWriter to capture the rendered HTML\n    using (StringWriter sw = new StringWriter())\n    {\n        // Use HtmlTextWriter to write to the StringWriter\n        using (HtmlTextWriter htmlWriter = new HtmlTextWriter(sw))\n        {\n            base.Render(htmlWriter);\n            \n            // Get the captured HTML\n            string renderedHtml = sw.ToString();\n            \n            // Log the HTML as needed, e.g., to a file or database\n            LogHtml(renderedHtml);  // Define your own logging method here\n        }\n    }\n\n    // Proceed with the normal rendering process\n    base.Render(writer);\n}\n\nprivate void LogHtml(string html)\n{\n    // Implement your logging logic here\n    // For example, writing to a log file:\n    System.IO.File.AppendAllText("path/to/your/logfile.html", html);\n}