using System.Net;\nusing System.Net.Mail;\n\npublic void SendEmail()\n{\n    SmtpClient client = new SmtpClient("smtp.example.com", 25);\n    \n    // Set your SMTP credentials if required\n    client.Credentials = new NetworkCredential("your-username", "your-password");\n    \n    MailMessage mailMessage = new MailMessage();\n    mailMessage.From = new MailAddress("your-email@example.com");\n    mailMessage.To.Add(new MailAddress("recipient@example.com"));\n    mailMessage.Subject = "Test Email";\n    mailMessage.Body = "This is a test email.";\n\n    client.Send(mailMessage);\n}