using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        List<string> foo = GetFoo(); // Assume this fetches the first list\n        List<string> bar = GetBar(); // Assume this fetches the second list\n\n        var setBar = new HashSet<string>(bar, StringComparer.InvariantCultureIgnoreCase);\n\n        // Find common elements (intersection)\n        var intersection = foo.Where(x => setBar.Contains(x)).ToList();\n\n        // Find elements in foo not in bar\n        var onlyInFoo = foo.Except(bar, StringComparer.InvariantCultureIgnoreCase).ToList();\n\n        // Find elements in bar not in foo\n        var onlyInBar = bar.Except(foo, StringComparer.InvariantCultureIgnoreCase).ToList();\n\n        Console.WriteLine("Intersection: " + string.Join(", ", intersection));\n        Console.WriteLine("Only in Foo: " + string.Join(", ", onlyInFoo));\n        Console.WriteLine("Only in Bar: " + string.Join(", ", onlyInBar));\n    }\n\n    static List<string> GetFoo()\n    {\n        return new List<string> { "apple", "Banana", "CHERRY" };\n    }\n\n    static List<string> GetBar()\n    {\n        return new List<string> { "banana", "Cherry", "Date" };\n    }\n}