using System;\nusing System.Configuration;\nusing System.Web.Configuration;\n\npublic class DatabaseConnectionProvider\n{\n    public static string GetConnectionString()\n    {\n        // Open the web configuration file in the specified physical path.\n        Configuration config = WebConfigurationManager.OpenWebConfiguration("~");\n\n        // Check if connection strings section exists and is loaded.\n        ConnectionStringsSection connStringsSection = (ConnectionStringsSection)config.GetSection("connectionStrings");\n        if (connStringsSection != null)\n        {\n            // Retrieve specific connection string by name.\n            var connectionStringSettings = connStringsSection.ConnectionStrings["YourConnectionStringName"];\n            return connectionStringSettings?.ConnectionString;\n        }\n\n        throw new Exception("Connection string not found.");\n    }\n}