using System;\nusing System.Web;\nusing Newtonsoft.Json;\n\npublic class UserController : Controller\n{\n    [HttpPost]\n    public JsonResult SaveUserPreferences(string timeZoneName, string userLocale)\n    {\n        // Store timeZoneName and userLocale in session or database as needed.\n        Session["TimeZone"] = timeZoneName;\n        Session["Culture"] = userLocale;\n\n        // For demonstration purposes, simply return the received data back\n        return Json(new { timeZoneName, userLocale });\n    }\n}