using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string[] fileList = { "part1.bin", "part2.bin", "part3.bin" }; // List of your split file parts\n        string outputFilePath = @"C:\path\to\output\file.bin"; // Path where you want the joined file\n\n        using (FileStream outputFileStream = new FileStream(outputFilePath, FileMode.Create, FileAccess.Write))\n        {\n            foreach (string part in fileList)\n            {\n                using (FileStream inputStream = new FileStream(part, FileMode.Open, FileAccess.Read))\n                {\n                    byte[] buffer = new byte[4096]; // Buffer for reading files\n                    int bytesRead;\n                    while ((bytesRead = inputStream.Read(buffer, 0, buffer.Length)) > 0)\n                    {\n                        outputFileStream.Write(buffer, 0, bytesRead);\n                    }\n                }\n            }\n        }\n\n        Console.WriteLine("Files have been joined successfully.");\n    }\n}