using System;\n   using System.ServiceModel.Discovery;\n\n   class DiscoveryClientExample\n   {\n       static void Main()\n       {\n           DiscoveryClient discoveryClient = new DiscoveryClient(new UdpDiscoveryEndpoint());\n\n           // Create the FindCriteria which holds the ServiceContract and scope information\n           FindCriteria criteria = new FindCriteria(typeof(IHelloWorldService));\n           criteria.MaxResults = 1;\n\n           FindResponse findResponse = discoveryClient.Find(criteria);\n\n           foreach (var endpoint in findResponse.Endpoints)\n           {\n               Console.WriteLine($"Found service at: {endpoint.Address.UdpUri}");\n           }\n       }\n   }