using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    static void Main()\n    {\n        // Get the type for Scripting.Dictionary\n        Type scDictType = Type.GetTypeFromProgID("Scripting.Dictionary");\n\n        // Create an instance of Scripting.Dictionary\n        dynamic dictionary = Activator.CreateInstance(scDictType);\n\n        // Use the dictionary like any other key-value store\n        dictionary.Add("key1", "value1");\n        dictionary.Add("key2", 42);\n        \n        Console.WriteLine(dictionary.Item("key1"));  // Output: value1\n        Console.WriteLine(dictionary.Item("key2"));  // Output: 42\n        \n        // Check if a key exists\n        bool hasKey = dictionary.Exists("key1");\n        Console.WriteLine(hasKey);  // Output: True\n\n        // Iterate over keys and values\n        foreach (var key in dictionary.Keys)\n        {\n            Console.WriteLine($"{key}: {dictionary.Item(key)}");\n        }\n    }\n}