using System;\nusing System.Collections.Generic;\n\npublic class Employee\n{\n    public string Id { get; set; }\n    public string Name { get; set; }\n\n    public Employee(string id, string name)\n    {\n        Id = id;\n        Name = name;\n    }\n}\n\npublic class Department\n{\n    public string Id { get; set; }\n    public List<Employee> Employees { get; set; } = new List<Employee>();\n\n    public Department(string id)\n    {\n        Id = id;\n    }\n\n    public void AddEmployee(Employee employee)\n    {\n        Employees.Add(employee);\n    }\n}\n\npublic class Organization\n{\n    private Dictionary<string, Department> departments = new Dictionary<string, Department>();\n\n    public Department GetDepartment(string departmentId)\n    {\n        return departments.TryGetValue(departmentId, out var department) ? department : null;\n    }\n\n    public void AddDepartment(Department department)\n    {\n        if (!departments.ContainsKey(department.Id))\n        {\n            departments.Add(department.Id, department);\n        }\n    }\n}