using System;\nusing System.IO;\nusing itextsharp.text.pdf;\nusing itextsharp.xmp;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string inputPdf = "input.pdf";\n        string outputPdf = "output.pdf";\n\n        using (FileStream existingFileStream = new FileStream(inputPdf, FileMode.Open, FileAccess.Read))\n        {\n            PdfReader pdfReader = new PdfReader(existingFileStream);\n\n            using (FileStream outputStream = new FileStream(outputPdf, FileMode.Create, FileAccess.Write))\n            {\n                PdfStamper pdfStamper = new PdfStamper(pdfReader, outputStream);\n                XfaForm xfaForm = pdfStamper.XFA;\n\n                // Access the XML document representing the form\n                XmlDocument xmlDoc = xfaForm.DomDocument;\n\n                // Navigate to the node you want to populate\n                XmlNode nodeToPopulate = xmlDoc.SelectSingleNode("//*[local-name()='yourFieldName']");\n                \n                if (nodeToPopulate != null)\n                {\n                    // Set the value of the field\n                    nodeToPopulate.InnerText = "Your Field Value";\n                }\n\n                // Update the XFA form with the modified XML document\n                xfaForm.Changed();\n\n                pdfStamper.FormFlattening = false; // Important: Do not flatten the form if you plan to edit it later.\n                pdfStamper.Close();\n            }\n        }\n    }\n}