using System;\nusing System.Collections.Generic;\nusing System.Data;\n\npublic class Example\n{\n    public static void Main()\n    {\n        // Assuming myDS, myTable, and myOtherTable are already populated\n        DataTable myTable = myDS.Tables["myTable"];\n        DataTable myOtherTable = myDS.Tables["myOtherTable"];\n\n        DataView dv_other = new DataView(myOtherTable);\n        HashSet<int> distinctParentIDs = new HashSet<int>();\n\n        foreach (DataRowView rowView in dv_other)\n        {\n            int parentID = Convert.ToInt32(rowView["parentID"]);\n            distinctParentIDs.Add(parentID);\n        }\n\n        // Building a filter string from the distinct parent IDs\n        List<string> filters = new List<string>();\n        foreach (int id in distinctParentIDs)\n        {\n            filters.Add($"id = {id}");\n        }\n        \n        string filterExpression = string.Join(" OR ", filters);\n\n        DataView dv = new DataView(myTable, filterExpression, "name asc", DataViewRowState.CurrentRows);\n    }\n}