using System;\nusing System.Collections.Generic;\n\npublic class Global : System.Web.HttpApplication\n{\n    private static HashSet<string> OnlineUsers = new HashSet<string>();\n\n    protected void Session_Start(object sender, EventArgs e)\n    {\n        // Add UserID to the online users set when a session starts\n        string userId = HttpContext.Current.Session["UserID"]?.ToString();\n        if (!string.IsNullOrEmpty(userId))\n        {\n            lock (OnlineUsers)\n                OnlineUsers.Add(userId);\n        }\n    }\n\n    protected void Session_End(object sender, EventArgs e)\n    {\n        // Remove UserID from the online users set when a session ends\n        string userId = HttpContext.Current.Session["UserID"]?.ToString();\n        if (!string.IsNullOrEmpty(userId))\n        {\n            lock (OnlineUsers)\n                OnlineUsers.Remove(userId);\n        }\n    }\n\n    public static List<string> GetOnlineUsers()\n    {\n        return new List<string>(OnlineUsers);\n    }\n}