using System;\nusing System.Threading;\n\npublic class UniqueIdGenerator\n{\n    private static int _counter = 0;\n    private static readonly object _lockObject = new object();\n\n    public static string GenerateUniqueId()\n    {\n        long time = DateTime.UtcNow.Ticks; // Use UTC to avoid issues with timezone changes\n        int counterValue;\n\n        lock (_lockObject)\n        {\n            _counter = (_counter + 1) % 1024; // Reset after reaching 1024 to save space and prevent overflow\n            counterValue = _counter;\n        }\n\n        return $"{time:X}{counterValue:D3}"; // Hex and decimal formatting for uniqueness\n    }\n}