using System;\nusing System.ServiceModel;\n\nclass Program\n{\n    static void Main()\n    {\n        var binding = new BasicHttpBinding();\n        var endpoint = new EndpointAddress("http://drs.displayscreen.service/Handler.svc");\n        \n        using (var channelFactory = new ChannelFactory<IMyServiceContract>(binding, endpoint))\n        {\n            var client = channelFactory.CreateChannel();\n\n            try\n            {\n                // Call your service method here.\n                Console.WriteLine(client.SomeMethod());\n            }\n            catch (CommunicationException ex)\n            {\n                Console.WriteLine($"Error: {ex.Message}");\n            }\n        }\n    }\n}