using System;\nusing System.Data;\n\npublic class Person\n{\n    public string Name { get; set; }\n    public decimal? Amount { get; set; } // Use nullable decimal type\n}\n\nclass Program\n{\n    static void Main()\n    {\n        DataSet ds = new DataSet();\n        ds.Tables.Add(new DataTable());\n        ds.Tables[0].Columns.Add("Name", typeof(string));\n        ds.Tables[0].Columns.Add("Amount", typeof(decimal));\n        ds.Tables[0].Rows.Add("My Name Here", DBNull.Value); // create row with a null item\n\n        DataRow dataRow = ds.Tables[0].Rows[0];\n\n        Person p = new Person\n        {\n            Name = (string)dataRow["Name"],\n            Amount = !dataRow.IsNull("Amount") ? Convert.ToDecimal(dataRow["Amount"]) : (decimal?)null\n        };\n\n        Console.WriteLine($"Name: {p.Name}, Amount: {(p.Amount.HasValue ? p.Amount.Value.ToString() : "null")}");\n    }\n}