void UseRef(ref int number)\n{\n    number = 10; // Can modify the argument's value\n}\n\nvoid UseOut(out int result)\n{\n    result = 20; // Must assign a value before exiting the method\n}\n\nint mainNumber;\nmainNumber = 5;\nUseRef(ref mainNumber); // ref requires initialization\nConsole.WriteLine(mainNumber); // Outputs "10" because mainNumber was modified\n\nint outputResult;\nUseOut(out outputResult); // out does not require (or allow) initialization\nConsole.WriteLine(outputResult); // Outputs "20"