using System;\nusing System.Data;\nusing System.Linq;\n\n// Assuming `dataTable` is your existing DataTable loaded with data.\nDataTable resultTable = dataTable.AsEnumerable()\n    .GroupBy(row => row.Field<string>("AddressA"))\n    .Select(g => new\n    {\n        AddressA = g.Key,\n        TotalBytes = g.Sum(r => r.Field<uint>("Bytes"))\n    })\n    .AsEnumerable()\n    .Select((item, index) => dataTable.NewRow().SetField("Id", item.index + 1)\n                                          .SetField("AddressA", item.AddressA)\n                                          .SetField("TotalBytes", item.TotalBytes))\n    .CopyToDataTable();\n\n// Adding a TotalBytes column to the result table.\nDataColumn totalBytesColumn = new DataColumn("TotalBytes", typeof(uint));\nresultTable.Columns.Add(totalBytesColumn);\n\n// Bind `resultTable` to your DataGrid