using System;\nusing System.Drawing;\nusing System.Drawing.Imaging;\nusing System.Web;\n\npublic class ChartHandler : IHttpHandler\n{\n    public void ProcessRequest(HttpContext context)\n    {\n        // Set the content type of the response to image/png\n        context.Response.ContentType = "image/png";\n\n        // Create a bitmap with a specified width and height\n        using (Bitmap bmp = new Bitmap(400, 200))\n        {\n            using (Graphics g = Graphics.FromImage(bmp))\n            {\n                // Optionally, set the background color of the image\n                g.Clear(Color.White);\n\n                // Draw some lines to represent a simple bar chart\n                g.DrawRectangle(Pens.Black, new Rectangle(50, 150, 300, 2));\n\n                g.FillRectangle(Brushes.CornflowerBlue, 50, 140, 75, 10); // Bar 1\n                g.FillRectangle(Brushes.DarkGreen, 150, 60, 75, 90);    // Bar 2\n                g.FillRectangle(Brushes.Coral, 250, 80, 75, 70);       // Bar 3\n\n                // Optionally, add text labels\n                using (Font font = new Font("Arial", 10))\n                    g.DrawString("Bar Chart", font, Brushes.Black, 150, 10);\n            }\n\n            // Save the image to the response output stream as a PNG\n            bmp.Save(context.Response.OutputStream, ImageFormat.Png);\n        }\n    }\n\n    public bool IsReusable => false;\n}