using System;\n   using System.Web.UI;\n\n   public class ApplicationBasePage : Page\n   {\n       protected override void OnInit(EventArgs e)\n       {\n           base.OnInit(e);\n           \n           // Connect to the database and retrieve the expiration date\n           DateTime expirationDate = GetExpirationDateFromDatabase();\n   \n           if (DateTime.Now > expirationDate)\n           {\n               throw new InvalidOperationException("Your license has expired. Please contact support for further assistance.");\n           }\n       }\n\n       private DateTime GetExpirationDateFromDatabase()\n       {\n           // Implement logic to connect to your database, retrieve the ExpirationDate,\n           // and return it as a DateTime object.\n           \n           using (System.Data.SqlClient.SqlConnection conn = new System.Data.SqlClient.SqlConnection("your-connection-string-here"))\n           {\n               string query = "SELECT [ExpirationDate] FROM [dbo].[ApplicationLicense]";\n               using (System.Data.SqlClient.SqlCommand cmd = new System.Data.SqlClient.SqlCommand(query, conn))\n               {\n                   conn.Open();\n                   object result = cmd.ExecuteScalar();\n                   if (result != null)\n                   {\n                       return Convert.ToDateTime(result);\n                   }\n                   throw new InvalidOperationException("License expiration date not found.");\n               }\n           }\n       }\n   }