using System;\nusing System.Net.NetworkInformation;\nusing System.Linq;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string[] ipAddresses = { "192.168.1.1", "192.168.1.2" }; // List of IP addresses to check\n\n        var tcpConnections = IPGlobalProperties.GetIPGlobalProperties()\n                                                  .GetActiveTcpConnections();\n\n        foreach (var ipAddress in ipAddresses)\n        {\n            bool isConnected = tcpConnections.Any(conn => conn.RemoteEndPoint.Address.ToString().Equals(ipAddress));\n            \n            Console.WriteLine($"Connection to {ipAddress}: {(isConnected ? "Active" : "Inactive")}");\n        }\n    }\n}