using System;\nusing System.Drawing;\nusing System.Windows.Forms;\n\npublic void SetImageBasedOnDPI(PictureBox pictureBox)\n{\n    Graphics graphics = Form.ActiveForm.CreateGraphics();\n    float dpiX = graphics.DpiX;\n    \n    if (dpiX > 180) // Assuming high DPI is above 180\n    {\n        pictureBox.Image = Image.FromFile("high_res_image.png");\n    }\n    else\n    {\n        pictureBox.Image = Image.FromFile("low_res_image.png");\n    }\n\n    graphics.Dispose();\n}