using (var db = new YourDataContext())\n{\n    var parentsWithChildrenDict = \n        from p in db.Parents\n        select new ParentWithChildren\n        {\n            Parent = p,\n            Children = p.Children.ToDictionary(c => c.ID, c => c.Name)\n        };\n\n    // Materialize the query to get the result as a list.\n    var parentsList = parentsWithChildrenDict.ToList();\n}\n\n// Define an additional class if needed for transforming data:\npublic class ParentWithChildren\n{\n    public Parent Parent { get; set; }\n    public Dictionary<int, string> Children { get; set; }\n}