using System;\nusing System.IO.Pipes;\n\npublic class Resources\n{\n    private static NamedPipeServerStream serverPipe;\n    private static string data = DateTime.Now.ToString();\n\n    static Resources()\n    {\n        StartNamedPipeServer();\n    }\n\n    private static void StartNamedPipeServer()\n    {\n        new System.Threading.Thread(() =>\n        {\n            while (true)\n            {\n                try\n                {\n                    using (serverPipe = new NamedPipeServerStream("SharedData"))\n                    {\n                        serverPipe.WaitForConnection();\n\n                        using (var writer = new StreamWriter(serverPipe))\n                        {\n                            writer.WriteLine(data);\n                            writer.Flush();\n                        }\n                    }\n                }\n                catch (Exception ex)\n                {\n                    Console.WriteLine($"Error: {ex.Message}");\n                }\n            }\n        }).Start();\n    }\n\n    public static string Data\n    {\n        get { return data; }\n    }\n}