using System.Configuration;\nusing Microsoft.Practices.EnterpriseLibrary.Common.Configuration;\nusing Microsoft.Practices.EnterpriseLibrary.Data;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Load configuration from app.config or web.config\n        var fileMap = new ExeConfigurationFileMap { ExeConfigFilename = @"path\to\your\app.config" };\n        Configuration config = ConfigurationManager.OpenMappedExeConfiguration(fileMap, ConfigurationUserLevel.None);\n\n        // Get data configuration section\n        var dataSettings = (DataConfigurationSettings)config.GetSection("dataConfiguration");\n\n        // Encrypt connection strings\n        foreach (ConnectionStringsSectionGroup group in config.ConnectionStrings)\n        {\n            if (!group.ConnectionStrings.SectionInformation.IsProtected)\n            {\n                group.ConnectionStrings.SectionInformation.ProtectSection("DataProtectionConfigurationProvider");\n                group.ConnectionStrings.SectionInformation.ForceSave = true;\n            }\n        }\n\n        // Save the encrypted configuration file\n        config.Save();\n    }\n}