using System;\nusing System.Drawing;\nusing Tesseract;\n\nclass Program\n{\n    static void Main()\n    {\n        // Path to the tessdata directory containing language files\n        const string langPath = @"./tessdata";\n\n        // Initialize Tesseract engine with path and language\n        using (var engine = new TesseractEngine(langPath, "eng", EngineMode.Default))\n        {\n            // Load the image you want to process\n            using (var img = Pix.LoadFromFile("path/to/your/image.png"))\n            {\n                using (var page = engine.Process(img))\n                {\n                    // Get text from the processed image\n                    string result = page.GetText();\n\n                    Console.WriteLine("Text in Image:");\n                    Console.WriteLine(result);\n                }\n            }\n        }\n    }\n}