using System;\nusing System.IO.Pipes;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        Console.WriteLine("Press enter to capture the resource!");\n        Console.ReadLine();\n\n        var sharedData = GetSharedResource();\n        Console.WriteLine($"Shared Resource: {sharedData}");\n\n        Console.WriteLine("Press enter to close the process!");\n        Console.ReadLine();\n    }\n\n    private static string GetSharedResource()\n    {\n        using (var clientPipe = new NamedPipeClientStream(".", "SharedData", PipeDirection.In))\n        {\n            clientPipe.Connect();\n\n            using (var reader = new StreamReader(clientPipe))\n            {\n                return reader.ReadLine();\n            }\n        }\n    }\n}