using System.IO;\nusing FileSignatures;\n\npublic class FileTypeChecker\n{\n    public static string GetFileContentType(byte[] fileBytes)\n    {\n        var detector = new FileTypeDetector();\n        return detector.GetFileType(fileBytes)?.Description ?? "Unknown";\n    }\n}\n\n// Usage example in your controller or service:\n[HttpPost("upload")]\npublic IActionResult UploadFile(IFormFile file)\n{\n    using (var memoryStream = new MemoryStream())\n    {\n        file.CopyTo(memoryStream);\n        var fileType = FileTypeChecker.GetFileContentType(memoryStream.ToArray());\n\n        if (fileType == "JPEG File Interchange Format")\n        {\n            // Proceed with the upload or further actions\n            return Ok("File is a JPEG");\n        }\n        else\n        {\n            return BadRequest($"Invalid file type. Detected: {fileType}");\n        }\n    }\n}