using System;\nusing System.IO;\nusing System.Net;\nusing System.Web.Script.Serialization;\n\npublic class AddressLookup\n{\n    private const string AuthId = "your-auth-id"; // Replace with your actual Auth ID\n    private const string AuthToken = "your-auth-token"; // Replace with your actual Auth Token\n\n    public static string GetCountyFromZip(string zipCode)\n    {\n        var url = $"https://us-street.api.smartystreets.com/street-addresses?auth-id={AuthId}&auth-token={AuthToken}";\n        var request = (HttpWebRequest)WebRequest.Create(url);\n        request.Method = "POST";\n        \n        using (var streamWriter = new StreamWriter(request.GetRequestStream()))\n        {\n            string jsonInput = $"[{{\"zip_code\": \"{zipCode}\"}}]";\n            streamWriter.Write(jsonInput);\n        }\n\n        try\n        {\n            var response = (HttpWebResponse)request.GetResponse();\n            using (StreamReader reader = new StreamReader(response.GetResponseStream()))\n            {\n                JavaScriptSerializer serializer = new JavaScriptSerializer();\n                dynamic addressResults = serializer.Deserialize<dynamic>(reader.ReadToEnd());\n                \n                if (addressResults.Count > 0 && addressResults[0].components != null)\n                {\n                    return addressResults[0].components.county_name;\n                }\n            }\n\n        }\n        catch (WebException ex)\n        {\n            Console.WriteLine("Error: " + ex.Message);\n        }\n\n        return "Unknown";\n    }\n}