// MVC Controller Example\npublic class HomeController : Controller\n{\n    private readonly IUserDataService _userDataService;\n\n    public HomeController(IUserDataService userDataService)\n    {\n        _userDataService = userDataService;\n    }\n\n    public ActionResult Index()\n    {\n        var users = _userDataService.GetAllUsers();\n        return View(users);\n    }\n}\n\n// Simple Test Case Example using MSTest\n[TestClass]\npublic class HomeControllerTest\n{\n    [TestMethod]\n    public void Index_Returns_View_With_User_Data()\n    {\n        // Arrange\n        var mockUserDataService = new Mock<IUserDataService>();\n        mockUserDataService.Setup(x => x.GetAllUsers()).Returns(new List<User> { new User { Id = 1, Name = "John Doe" } });\n        \n        var controller = new HomeController(mockUserDataService.Object);\n\n        // Act\n        var result = controller.Index() as ViewResult;\n\n        // Assert\n        Assert.IsNotNull(result);\n        Assert.IsInstanceOfType(result.Model, typeof(List<User>));\n    }\n}