using System;\nusing System.Reactive.Subjects;\n\nclass Program\n{\n    static void Main()\n    {\n        var subject = new Subject<int>();\n        \n        // Subscribe to the observable sequence.\n        subject.Subscribe(\n            number => Console.WriteLine(number),\n            () => Console.WriteLine("Completed!")\n        );\n\n        // Push data into the observable sequence.\n        subject.OnNext(1);  // Output: 1\n        subject.OnNext(2);  // Output: 2\n\n        // Indicate that no more events will be emitted.\n        subject.OnCompleted();  // Output: Completed!\n    }\n}