using System;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        string xmlFilePath = "path/to/your/xmlfile.xml";\n        string xsdFilePath = "path/to/your/xsdfile.xsd";\n\n        XmlReaderSettings settings = new XmlReaderSettings();\n        settings.Schemas.Add(null, xsdFilePath);\n        settings.ValidationType = ValidationType.Schema;\n        settings.ValidationEventHandler += delegate (object sender, ValidationEventArgs args)\n        {\n            Console.WriteLine($"Validation Error: {args.Message}");\n            IXmlLineInfo lineInfo = sender as IXmlLineInfo;\n            if (lineInfo != null)\n            {\n                Console.WriteLine($"At Line Number: {lineInfo.LineNumber}");\n                Console.WriteLine($"At Position: {lineInfo.LinePosition}");\n            }\n        };\n\n        using (XmlReader rdr = XmlReader.Create(xmlFilePath, settings))\n        {\n            while (rdr.Read()) { } // No need to do anything in here for validation\n        }\n\n        Console.WriteLine("Validation Complete.");\n    }\n}