using System;\nusing System.Net.Http;\nusing System.Text;\nusing System.Threading.Tasks;\n\nclass Program\n{\n    static async Task Main()\n    {\n        string username = "user";\n        string password = "pass";\n\n        using (var client = new HttpClient())\n        {\n            client.DefaultRequestHeaders.UserAgent.ParseAdd("Mozilla/4.0 (compatible; MSIE 6.0; Windows NT 5.1; .NET CLR 1.0.3705)");\n\n            var content = new FormUrlEncodedContent(new[]\n            {\n                new KeyValuePair<string, string>("nick", username),\n                new KeyValuePair<string, string>("password", password)\n            });\n\n            try\n            {\n                HttpResponseMessage response = await client.PostAsync("https://moje.azet.sk/prihlasenie.phtml?KDE=www.azet.sk%2Findex.phtml%3F", content);\n\n                if (response.IsSuccessStatusCode)\n                {\n                    string responseBody = await response.Content.ReadAsStringAsync();\n                    Console.WriteLine(responseBody);\n                }\n                else\n                {\n                    Console.WriteLine($"Request failed with status code: {response.StatusCode}");\n                }\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"An error occurred: {ex.Message}");\n            }\n        }\n    }\n}