using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\npublic class Program\n{\n    public static void Main()\n    {\n        var data = new List<Person>\n        {\n            new Person { Name = "Bob", Hobby = "Football" },\n            new Person { Name = "Bob", Hobby = "Golf" },\n            new Person { Name = "Bob", Hobby = "Tennis" },\n            new Person { Name = "Sue", Hobby = "Sleeping" },\n            new Person { Name = "Sue", Hobby = "Drinking" }\n        };\n\n        var result = data\n            .GroupBy(p => p.Name)\n            .Select(g => new { User = g.Key, Hobbies = string.Join(", ", g.Select(h => h.Hobby)) })\n            .ToList();\n\n        foreach (var item in result)\n        {\n            Console.WriteLine($"{item.User}   {item.Hobbies}");\n        }\n    }\n\n    public class Person\n    {\n        public string Name { get; set; }\n        public string Hobby { get; set; }\n    }\n}