using System.Net.Http;\nusing System.Text.Json;\nusing System.Threading.Tasks;\n\npublic class ApplicationWrapper\n{\n    private readonly HttpClient _httpClient = new HttpClient { BaseAddress = new Uri("https://yourappserver.com/api/") };\n\n    public async Task<string> PlaceCallAsync(string phoneNumber)\n    {\n        var response = await _httpClient.PostAsJsonAsync("Operations/placeCall", phoneNumber);\n        response.EnsureSuccessStatusCode();\n        return await response.Content.ReadAsStringAsync();\n    }\n}