using System;\nusing System.Xml;\nusing System.Xml.XPath;\n\npublic class Program\n{\n    public static void Main()\n    {\n        XmlDocument myXML = new XmlDocument();\n        myXML.Load("yourfile.xml"); // Load your XML document here\n\n        XPathNavigator navigator = myXML.CreateNavigator();\n\n        // Define the namespace and set it in XmlNamespaceManager\n        XmlNamespaceManager nsmgr = new XmlNamespaceManager(navigator.NameTable);\n        string defaultNamespace = "http://example.com/defaultns"; // Replace with your actual namespace URI\n        nsmgr.AddNamespace("def", defaultNamespace); // 'def' is a prefix we're using for this example\n\n        // Use the prefix in your XPath query\n        XPathNodeIterator result = navigator.Select("/def:outerelement/def:innerelement", nsmgr);\n\n        while (result.MoveNext())\n        {\n            Console.WriteLine(result.Current.OuterXml);\n        }\n    }\n}