public void ExecuteWithRetry(Action<SqlConnection> action)\n{\n    int maxRetries = 5;\n    for (int attempt = 0; attempt < maxRetries; attempt++)\n    {\n        try\n        {\n            using (var connection = new SqlConnection(connectionString))\n            {\n                connection.Open();\n                action(connection);\n            }\n            break; // Exit if successful\n        }\n        catch (SqlException ex)\n        {\n            // Check for specific transient error codes or messages here if necessary\n            if (attempt == maxRetries - 1) throw; // rethrow on last attempt\n\n            System.Threading.Thread.Sleep(300); // Wait a bit before retrying\n        }\n    }\n}