using System.Drawing;\n\npublic void CopyGraphics()\n{\n    // Create a bitmap with the same size as the panel\n    using (Bitmap srcBmp = new Bitmap(pnl.Width, pnl.Height))\n    {\n        // Create a Graphics object from the bitmap\n        using (Graphics srcMem = Graphics.FromImage(srcBmp))\n        {\n            // Draw the panel onto the bitmap\n            srcMem.DrawRectangle(Pens.Black, 0, 0, pnl.Width - 1, pnl.Height - 1); // Just an example drawing\n            srcPic.DrawToBitmap(srcBmp, new System.Drawing.Rectangle(0, 0, pnl.Width, pnl.Height));\n        }\n\n        // Set the bitmap as the background image of another panel or use it as needed\n        pnlDraw.BackgroundImage = srcBmp;\n    }\n}