// Example: Using Entity Framework (ORM) for quick development\npublic class UserRepository : IUserRepository\n{\n    private readonly MyAppDbContext _context;\n\n    public UserRepository(MyAppDbContext context)\n    {\n        _context = context;\n    }\n\n    public User GetUserById(int id)\n    {\n        return _context.Users.FirstOrDefault(u => u.UserId == id);\n    }\n}\n\n// Example: Direct SQL for performance-critical section\npublic class OptimizedUserRepository : IUserRepository\n{\n    private readonly string _connectionString;\n\n    public OptimizedUserRepository(string connectionString)\n    {\n        _connectionString = connectionString;\n    }\n\n    public User GetUserById(int id)\n    {\n        using (var connection = new SqlConnection(_connectionString))\n        {\n            var command = new SqlCommand("GetUserById", connection)\n            {\n                CommandType = CommandType.StoredProcedure\n            };\n            command.Parameters.AddWithValue("@UserId", id);\n            connection.Open();\n            using (var reader = command.ExecuteReader())\n            {\n                if (reader.Read())\n                {\n                    return new User\n                    {\n                        UserId = reader.GetInt32(0),\n                        Username = reader.GetString(1)\n                    };\n                }\n            }\n        }\n\n        return null;\n    }\n}