protected void btnUpdate_Click(object sender, EventArgs e)\n{\n    var customer = new Customer { /* Initialize customer */ };\n    \n    // Reflection to get all methods decorated with EntityAuthorizeAttribute\n    var methodInfo = typeof(CustomerPage).GetMethod("UpdateCustomer");\n    var attributes = (EntityAuthorizeAttribute[])methodInfo.GetCustomAttributes(typeof(EntityAuthorizeAttribute), false);\n\n    if (attributes.Length > 0 && !new AccessManager().HasPermission(Session, attributes[0].Permission))\n    {\n        // Handle unauthorized attempt\n        Response.Write("Unauthorized access attempt.");\n        return;\n    }\n\n    UpdateCustomer(customer);\n}