using System;\nusing System.Xml;\nusing System.Xml.XPath;\n\npublic class XPathExample\n{\n    public XPathDocument CreateXPathDocumentFromXmlElement(XmlElement xmlElement)\n    {\n        // Convert XmlElement to string\n        using (var stringWriter = new System.IO.StringWriter())\n        {\n            using (var xmlTextWriter = new XmlTextWriter(stringWriter))\n            {\n                xmlElement.WriteTo(xmlTextWriter);\n                string xmlString = stringWriter.ToString();\n\n                // Load the string into an XPathDocument\n                using (var xmlReader = new XmlTextReader(new System.IO.StringReader(xmlString)))\n                {\n                    XPathDocument xpathDoc = new XPathDocument(xmlReader);\n                    return xpathDoc;\n                }\n            }\n        }\n    }\n}