public interface IContactInfo {}\n\npublic class Address : IContactInfo {}\npublic class Email : IContactInfo {}\npublic class Phone : IContactInfo {}\n\npublic class ContactManager \n{\n    private List<Address> m_Address = new List<Address>();\n    private List<Email> m_Email = new List<Email>();\n    private List<Phone> m_Phone = new List<Phone>();\n\n    public void Add<T>(T obj) where T : IContactInfo\n    {\n        if (obj is Address address)\n            m_Address.Add(address);\n        else if (obj is Email email)\n            m_Email.Add(email);\n        else if (obj is Phone phone)\n            m_Phone.Add(phone);\n    }\n}