using System;\nusing System.Management;\n\nclass Program\n{\n    static void Main()\n    {\n        // Use the Image device class if Win32_Scanner isn't available\n        string imageDeviceClassGuid = "4658ee7e-f08b-11d0-b6b1-00a0c90f57da"; // This is the GUID for image devices\n\n        ManagementObjectSearcher search = new ManagementObjectSearcher(\n            $"SELECT * FROM Win32_PnPEntity WHERE DeviceID LIKE 'Image%*{imageDeviceClassGuid}*'");\n\n        ManagementObjectCollection deviceCollection = search.Get();\n\n        foreach (ManagementObject info in deviceCollection)\n        {\n            string deviceName = Convert.ToString(info["Caption"]);\n            Console.WriteLine(deviceName);\n            // Add a scanner to the list or perform any further actions\n        }\n    }\n}