using System;\nusing System.Data.SqlClient;\n\npublic class SqlClientExample\n{\n    public void RunQuery()\n    {\n        SqlConnection sqlConnection = null;\n        try\n        {\n            sqlConnection = new SqlConnection("your_connection_string_here");\n            sqlConnection.Open();\n            // Perform database operations here...\n        }\n        catch (Exception ex)\n        {\n            // Handle exceptions here...\n        }\n        finally\n        {\n            if (sqlConnection != null && sqlConnection.State == System.Data.ConnectionState.Open)\n            {\n                sqlConnection.Close(); // Ensure the connection is closed\n            }\n\n            sqlConnection?.Dispose(); // Dispose of the object to release unmanaged resources\n            // Setting to null after dispose can be optional, but some prefer it.\n            sqlConnection = null; \n        }\n    }\n}