using System;\nusing System.Net.Mail;\n\nclass Program\n{\n    static void Main()\n    {\n        SmtpClient client = new SmtpClient("smtp.yourdomain.com"); // Use your SMTP server address\n        MailMessage mailMessage = new MailMessage();\n        mailMessage.From = new MailAddress("you@yourdomain.com");\n        mailMessage.To.Add(new MailAddress("recipient@example.com"));\n        mailMessage.Subject = "Test Email";\n        mailMessage.Body = "Hello, this is a test email.";\n\n        try\n        {\n            client.Send(mailMessage);\n            Console.WriteLine("Email sent successfully.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Failed to send email: " + ex.Message);\n        }\n    }\n}