using System;\nusing System.IO;\nusing System.Net;\nusing System.Text;\nusing System.Web.Script.Serialization;\n\npublic class Person \n{\n    public string firstName { get; set; }\n    public string lastName { get; set; }\n    public string email { get; set; }\n    public string deviceUUID { get; set; }\n}\n\npublic void SendJson()\n{\n    Person p = new Person();\n    p.firstName = "Bill";\n    p.lastName = "Gates";\n    p.email = "asdf@hotmail.com";\n    p.deviceUUID = "abcdefghijklmnopqrstuvwxyz";\n\n    HttpWebRequest request = (HttpWebRequest)WebRequest.Create(targetUri + "newuser.php");\n    request.Method = "POST";\n    request.ContentType = "application/json"; // Changed Content-Type\n\n    JavaScriptSerializer serializer = new JavaScriptSerializer();\n    string jsonData = serializer.Serialize(p); \n\n    byte[] byteData = Encoding.UTF8.GetBytes(jsonData);\n\n    request.ContentLength = byteData.Length;\n    using (Stream dataStream = request.GetRequestStream())\n    {\n        dataStream.Write(byteData, 0, byteData.Length);\n    }\n\n    using (WebResponse response = request.GetResponse())\n    {\n        using (Stream stream = response.GetResponseStream())\n        {\n            using (StreamReader reader = new StreamReader(stream))\n            {\n                string responseFromServer = reader.ReadToEnd();\n                textBox4.Text += responseFromServer;\n            }\n        }\n    }\n}