using System;\nusing HtmlAgilityPack;\n\nclass Program\n{\n    static void Main()\n    {\n        string html = @"<html><body>\n                        <a href='http://example1.com'>Example 1</a>\n                        <a href='http://example2.com'>Example 2</a>\n                      </body></html>";\n\n        var doc = new HtmlDocument();\n        doc.LoadHtml(html);\n\n        // Select all anchor tags\n        var nodes = doc.DocumentNode.SelectNodes("//a[@href]");\n\n        if (nodes != null)\n        {\n            foreach (var node in nodes)\n            {\n                string hrefValue = node.GetAttributeValue("href", "");\n                Console.WriteLine(hrefValue);  // Output the href attribute value\n                // You can store hrefValue in a list or any other collection for later use.\n            }\n        }\n    }\n}