using System;\nusing System.Collections.Generic;\nusing ShamirSecretSharing;\n\nclass Program\n{\n    static void Main()\n    {\n        var shamir = new Shamir(5, 3); // Create a threshold scheme that needs 3 out of 5 parts\n\n        string secret = "SuperSecretKey";\n        List<string> shares = shamir.Split(secret);\n        \n        Console.WriteLine("Shares:");\n        foreach (var share in shares)\n            Console.WriteLine(share);\n\n        var recovered = shamir.Combine(shares.GetRange(0, 3)); // Using the first 3 parts to recover\n        Console.WriteLine($"Recovered Secret: {recovered}");\n    }\n}