using System;\nusing System.Net.Mail;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create a new MailMessage object and populate it with the sender's email,\n        // recipient's email, subject line, and message body content.\n        MailMessage mailMessage = new MailMessage();\n        mailMessage.From = new MailAddress("from@example.com");\n        mailMessage.To.Add(new MailAddress("to@example.com"));\n        mailMessage.Subject = "This is a test email subject";\n        mailMessage.IsBodyHtml = true;\n        mailMessage.Body = "<h1>This is a test email body</h1>";\n\n        // Create an attachment from the file and add it to the message.\n        Attachment attachment = new Attachment("testfile.txt");\n        mailMessage.Attachments.Add(attachment);\n\n        // Initialize an SmtpClient object with the SMTP server address.\n        SmtpClient smtpClient = new SmtpClient();\n        smtpClient.Host = "localhost"; // Localhost SMTP\n\n        try\n        {\n            // Send the message.\n            smtpClient.Send(mailMessage);\n            Console.WriteLine("Email sent successfully to pickup directory.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error: " + ex.Message);\n        }\n\n        // Clean up.\n        attachment.Dispose();\n        mailMessage.Dispose();\n    }\n}